import React, { useState } from "react";

export default function ProductForm({ onAdd }) {
  const [form, setForm] = useState({
    name: "",
    price: "",
    stock: "",
    manufacturer: "",
    expiry: "",
    category: "",
  });
  const [error, setError] = useState(null);

  function handleChange(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  function handleSubmit(e) {
    e.preventDefault();
    if (!form.name || !form.price || !form.stock || !form.expiry) {
      setError("Please fill all required fields.");
      return;
    }
    setError(null);
    onAdd(form);
    setForm({
      name: "",
      price: "",
      stock: "",
      manufacturer: "",
      expiry: "",
      category: "",
    });
  }

  return (
    <form onSubmit={handleSubmit} className="bg-white p-4 rounded-xl shadow mt-8">
      <h3 className="font-semibold text-lg mb-2">Add New Medicine</h3>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <input
          className="border p-2 rounded"
          name="name"
          value={form.name}
          onChange={handleChange}
          placeholder="Medicine Name *"
        />
        <input
          className="border p-2 rounded"
          name="price"
          value={form.price}
          onChange={handleChange}
          type="number"
          placeholder="Price (Rs.) *"
        />
        <input
          className="border p-2 rounded"
          name="stock"
          value={form.stock}
          onChange={handleChange}
          type="number"
          placeholder="Stock *"
        />
        <input
          className="border p-2 rounded"
          name="manufacturer"
          value={form.manufacturer}
          onChange={handleChange}
          placeholder="Manufacturer"
        />
        <input
          className="border p-2 rounded"
          name="expiry"
          value={form.expiry}
          onChange={handleChange}
          placeholder="Expiry (MM/YYYY) *"
        />
        <input
          className="border p-2 rounded"
          name="category"
          value={form.category}
          onChange={handleChange}
          placeholder="Category"
        />
      </div>
      {error && <div className="text-red-600 mt-2">{error}</div>}
      <button
        type="submit"
        className="mt-4 bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
      >
        Add
      </button>
    </form>
  );
}
