//ProductForm.jsx
import React, { useState } from "react";

export function ProductForm({ onAdd }) {
  const [form, setForm] = useState({
    name: "",
    price: "",
    stock: "",
    manufacturer: "",
    expiry: "",
    category: "",
  });
  const [error, setError] = useState("");

  function handleChange(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  function handleSubmit(e) {
    e.preventDefault();
    if (!form.name || !form.price || !form.stock || !form.expiry) {
      setError("Please fill all required fields.");
      return;
    }
    setError("");
    onAdd(form);
    setForm({
      name: "",
      price: "",
      stock: "",
      manufacturer: "",
      expiry: "",
      category: "",
    });
  }

  return (
    <form className="productform" onSubmit={handleSubmit}>
      <div className="productform__title">Add New Medicine</div>
      <div className="productform__fields">
        <input
          name="name"
          value={form.name}
          onChange={handleChange}
          placeholder="Medicine Name *"
          autoComplete="off"
        />
        <input
          name="price"
          type="number"
          value={form.price}
          onChange={handleChange}
          placeholder="Price *"
          min={1}
        />
        <input
          name="stock"
          type="number"
          value={form.stock}
          onChange={handleChange}
          placeholder="Stock *"
          min={0}
        />
        <input
          name="manufacturer"
          value={form.manufacturer}
          onChange={handleChange}
          placeholder="Manufacturer"
        />
        <input
          name="expiry"
          value={form.expiry}
          onChange={handleChange}
          placeholder="Expiry (MM/YYYY) *"
        />
        <input
          name="category"
          value={form.category}
          onChange={handleChange}
          placeholder="Category"
        />
      </div>
      {error && <div className="productform__error">{error}</div>}
      <button className="productform__submit" type="submit">Add Medicine</button>
    </form>
  );
}
