import React, { useState } from "react";
import { Plus, Pill, Package, BoxSelect, Calendar, Search, X } from "lucide-react";

export function ProductForm({ onAdd }) {
  const [form, setForm] = useState({
    name: "",
    price: "",
    stock: "",
    manufacturer: "",
    expiry: "",
    category: "",
  });
  const [error, setError] = useState(null);

  function handleChange(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  function handleSubmit(e) {
    e.preventDefault();
    if (!form.name || !form.price || !form.stock || !form.expiry) {
      setError("Please fill all required fields.");
      return;
    }
    setError(null);
    onAdd(form);
    setForm({
      name: "",
      price: "",
      stock: "",
      manufacturer: "",
      expiry: "",
      category: "",
    });
  }

  return (
    <form onSubmit={handleSubmit} className="bg-white rounded-2xl shadow-lg border border-gray-100 overflow-hidden">
      <div className="bg-gradient-to-r from-emerald-500 to-teal-500 p-4">
        <h3 className="font-bold text-lg text-white flex items-center">
          <Plus size={20} className="mr-2" />
          Add New Medicine
        </h3>
      </div>
      <div className="p-5">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="relative">
            <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
              <Pill className="text-gray-400" size={16} />
            </div>
            <input
              className="border border-gray-200 p-3 pl-10 rounded-xl w-full focus:ring-2 focus:ring-teal-500 focus:border-transparent outline-none transition"
              name="name"
              value={form.name}
              onChange={handleChange}
              placeholder="Medicine Name *"
            />
          </div>
          <div className="relative">
            <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
              <span className="text-gray-400 text-sm">Rs.</span>
            </div>
            <input
              className="border border-gray-200 p-3 pl-10 rounded-xl w-full focus:ring-2 focus:ring-teal-500 focus:border-transparent outline-none transition"
              name="price"
              value={form.price}
              onChange={handleChange}
              type="number"
              placeholder="Price *"
            />
          </div>
          <div className="relative">
            <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
              <Package className="text-gray-400" size={16} />
            </div>
            <input
              className="border border-gray-200 p-3 pl-10 rounded-xl w-full focus:ring-2 focus:ring-teal-500 focus:border-transparent outline-none transition"
              name="stock"
              value={form.stock}
              onChange={handleChange}
              type="number"
              placeholder="Stock *"
            />
          </div>
          <div className="relative">
            <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
              <BoxSelect className="text-gray-400" size={16} />
            </div>
            <input
              className="border border-gray-200 p-3 pl-10 rounded-xl w-full focus:ring-2 focus:ring-teal-500 focus:border-transparent outline-none transition"
              name="manufacturer"
              value={form.manufacturer}
              onChange={handleChange}
              placeholder="Manufacturer"
            />
          </div>
          <div className="relative">
            <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
              <Calendar className="text-gray-400" size={16} />
            </div>
            <input
              className="border border-gray-200 p-3 pl-10 rounded-xl w-full focus:ring-2 focus:ring-teal-500 focus:border-transparent outline-none transition"
              name="expiry"
              value={form.expiry}
              onChange={handleChange}
              placeholder="Expiry (MM/YYYY) *"
            />
          </div>
          <div className="relative">
            <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
              <Search className="text-gray-400" size={16} />
            </div>
            <input
              className="border border-gray-200 p-3 pl-10 rounded-xl w-full focus:ring-2 focus:ring-teal-500 focus:border-transparent outline-none transition"
              name="category"
              value={form.category}
              onChange={handleChange}
              placeholder="Category"
            />
          </div>
        </div>
        {error && (
          <div className="mt-4 bg-red-50 text-red-600 p-3 rounded-xl flex items-center">
            <X size={16} className="mr-2 flex-shrink-0" />
            {error}
          </div>
        )}
        <button
          type="submit"
          className="mt-6 bg-gradient-to-r from-emerald-500 to-teal-500 text-white px-6 py-3 rounded-xl hover:from-emerald-600 hover:to-teal-600 transition font-medium flex items-center justify-center w-full md:w-auto md:ml-auto"
        >
          <Plus size={18} className="mr-2" />
          Add Medicine
        </button>
      </div>
    </form>
  );
}
