import React from "react";

export default function Cart({ cart, onRemove }) {
  const total = cart.reduce(
    (sum, item) => sum + Number(item.price) * (item.quantity || 1),
    0
  );
  return (
    <div className="bg-white p-4 rounded-xl shadow mt-8">
      <h3 className="font-semibold text-lg mb-2">Cart</h3>
      {cart.length === 0 ? (
        <div className="text-gray-500">Cart is empty.</div>
      ) : (
        <>
          <ul>
            {cart.map((item) => (
              <li key={item._id} className="flex justify-between items-center py-2 border-b">
                <span>
                  {item.name} x {item.quantity || 1}
                </span>
                <span>Rs. {Number(item.price) * (item.quantity || 1)}</span>
                <button
                  className="ml-4 text-red-500 hover:underline"
                  onClick={() => onRemove(item._id)}
                >
                  Remove
                </button>
              </li>
            ))}
          </ul>
          <div className="mt-4 font-bold">Total: Rs. {total}</div>
        </>
      )}
    </div>
  );
}
